```c++
#include <iostream>
#include <algorithm>
#include <vector>

using namespace std;
int a[1020], b[1020];
int dp[1020][1020];
int main() {
    int n;
    cin>>n;
    for(int i=1;i<=n;i++) {
        cin>>a[i];
    }
    for(int i=1;i<=n;i++) {
        cin>>b[i];
    }
    for(int i=0;i<=n;i++) {
        for(int j=0;j<=n;j++) {
            dp[i][j]=1e9;
        }
    }
    dp[0][0]=0;
    for(int i=0;i<=n;i++) {
        for(int j=0;j<=n;j++) {
            int v=dp[i][j]+1;
            if(i<n) {
                if((a[i+1]+v)%2==0)
                    dp[i+1][j]=min(dp[i+1][j], dp[i][j]+1);
                else 
                    dp[i+1][j]=min(dp[i+1][j], dp[i][j]+2);
            }
            if(j<n) {
                if((b[j+1]+v)%2==0) 
                    dp[i][j+1]=min(dp[i][j+1], dp[i][j]+1);
                else 
                    dp[i][j+1]=min(dp[i][j+1], dp[i][j]+2);
            }
        }
    }
    cout<<dp[n][n]<<"\n";
}

